# Error Occurred if only urllib is imported. e.g. -
# import urllib
# Need to give specific library for importing
import urllib.request

website = "https://www.google.com"

def responseCode():
    print("Inside responseCode Method")
    print("Fetching data for - " + website)
    
    # To get the response code - 
    objURL = urllib.request.urlopen(website)
    print("Response code recieved - " + str(objURL.getcode()))

responseCode()
'''
Inside responseCode Method
Fetching data for - https://www.google.com
Response code recieved - 200
'''

def getHTMLData():

    objURL = urllib.request.urlopen(website)

    # To get the HTML data of the website
    urlHTMLData = objURL.read()
    print("HTML data for website - " + website,end='\n\n')
    print(urlHTMLData)
    
getHTMLData()

